window.addEventListener('load', function() {
  $(".blog.section_blog_news").each(function() {
    var this_blog = $(this);
    var news = this_blog.attr('data-cate');
    $.ajax({
      url: "/feeds/posts/default/-/" + news,
      type: "get",
      data: {
        alt: "json",
        "max-results": 5
      },
      dataType: "jsonp",
      success: function(e) {
        this_blog.removeClass("loader");
        var b = e.feed.entry;
        if (void 0 !== b) {
          for (var n = 0; n < e.feed.entry.length; n++) {
            var entry = e.feed.entry[n];
            for (var s = 0; s < e.feed.entry[n].link.length; s++) {
              if (e.feed.entry[n].link[s].rel === "alternate") {
                t = e.feed.entry[n].link[s].href;
                break
              }
            }
            var f = e.feed.entry[n].title.$t;
            if ('media$thumbnail' in entry) {
              var d = entry.media$thumbnail.url.replace('s72-c', 'w480-h270-p-k-no-nu')
            } else {
              var st = entry.content.$t,
                at = st.indexOf("<img"),
                bt = st.indexOf('src="', at),
                ct = st.indexOf('"', bt + 5),
                dt = st.substr(bt + 5, ct - bt - 5)
              if (at != -1 && bt != -1 && ct != -1 && dt != "") {
                d = dt.replace('s320', 'w480-h270--p-k-no-nu')
              } else {
                d = 'https://4.bp.blogspot.com/-00O66C-eBQs/W0IcokXSnOI/AAAAAAAAL_k/g4KtDm7SkQsoe7_G0vZ_C_nU0Gf_-kyVQCLcBGAs/w480-h270--p-k-no-nu/safe_image.png'
              }
            }
            if ("content" in e.feed.entry[n]) var post = e.feed.entry[n].content.$t;
            else post = "summary" in e.feed.entry[n] ? e.feed.entry[n].summary.$t : "";
            var post_content = post.replace(/<\S[^>]*>/g, "");
            var chuoi = e.feed.entry[n].published.$t,
              nam = chuoi.substring(0, 4),
              thang = chuoi.substring(5, 7),
              ngay = chuoi.substring(8, 10);
            if (n == 0) {
              var post_item_1 = '<a class="image-blog-left" href="' + t + '" title="' + f + '"> <img src="' + d + '" title="' + f + '" alt="' + f + '"> </a> <div class="content_blog"> <h3> <a href="' + t + '" title="' + f + '">' + f + '</a> </h3> <span class="date">' + ngay + '/' + thang + '/' + nam + '</span> <p> ' + post_content.substring(0, 90) + '...</p> </div>';
              this_blog.find('.itemblog_large').append(post_item_1)
            } else {
              var post_item_2 = '<div class="wrap_blogitem"> <a class="image-blog-left" href="' + t + '" title="' + f + '"> <img src="' + d + '" title="' + f + '" alt="' + f + '"> </a> <div class="content_blog"> <h3> <a href="' + t + '" title="' + f + '">' + f + '</a> </h3> <span class="date">' + ngay + '/' + thang + '/' + nam + '</span> </div> </div>';
              this_blog.find('.itemblog_mini').append(post_item_2)
            }
          }
        } else this_blog.html('<div class="fix-nof" style = "width: 100%; float: left;"> <div class="alert alert-warning show green-alert margin-bottom-0" role="alert"> <button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">×</span><span class="sr-only">Close</span></button> Nhãn: <strong style = "color:#000">' + news + ' </strong> chưa có bài viết.</div> </div>')
      },
      error: function() {
        this_blog.append("<strong>Error Loading Feed!</strong>")
      }
    })
  })
})
