                at = st.indexOf("<img"),
                bt = st.indexOf('src="', at),
                ct = st.indexOf('"', bt + 5),
                dt = st.substr(bt + 5, ct - bt - 5)
              if (at != -1 && bt != -1 && ct != -1 && dt != "") {
                d = dt.replace('s320', 'w480-h270--p-k-no-nu')
              } else {
                d = 'https://4.bp.blogspot.com/-00O66C-eBQs/W0IcokXSnOI/AAAAAAAAL_k/g4KtDm7SkQsoe7_G0vZ_C_nU0Gf_-kyVQCLcBGAs/w480-h270--p-k-no-nu/safe_image.png'
              }
            }

              var post_item = '<article class="blog-item blog-item-list "> <div class="blog-item-thumbnail img1"> <a href="'+t+'" title="'+f+'"> <img src="'+d+'" style="max-width:100%;" class="img-responsive" alt="'+f+'"> </a> </div> <div class="ct_list_item"> <h3 class="blog-item-name"><a href="'+t+'" title="'+f+'">'+f+'</a></h3> </div> </article>';
              this_blog.append(post_item);

          }
        } else this_blog.html('<div class="fix-nof" style = "width: 100%; float: left;"> <div class="alert alert-warning show green-alert margin-bottom-0" role="alert"> <button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">×</span><span class="sr-only">Close</span></button> Chưa có bài viết.</div> </div>')
      },
      error: function() {
        this_blog.append("<strong>Error Loading Feed!</strong>")
      }
    })
  })
})
